import React from 'react';
import '../App.css';


class Invoice extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        editEnabled: false,
        editInvoiceID: null
    }
  }

  enableEdit = (invoiceID) => {
    this.setState({
        editEnabled: true,
        editInvoiceID: invoiceID
    });
  }

  saveChanges = () => {
    this.props.updateInvoice()
  }

  render() {
    const {invoice} = this.props;
    const {editEnabled} = this.state;

    return (
         <div key={invoice.ID}>
            <span className="cell">{invoice.ID}</span>
            <span className="cell">
                {editEnabled
                    ? <input type="text" name="ClientName" value={invoice.ClientName} />
                    : invoice.ClientName
                }
            </span>
            <span className="cell">
                {editEnabled
                    ? <input type="text" name="amount" value={invoice.Amount} />
                    : invoice.Amount
                }
            </span>
            <span className="cell">
                {invoice.isPaid ? 'Paid' : 'Not Paid'}
            </span>
            <span className="cell">
                {!editEnabled
                    ?  <button onClick={this.enableEdit}>Edit</button>
                    :  <button onClick={this.saveChanges}>Save</button>
                }
            </span>
        </div>
    );
  }
}

export default Invoice;
