import React from 'react';
import '../App.css';


class Invoice extends React.Component {
  constructor(props) {
    super(props);
    const {invoice} = this.props;
    this.state = {
        editEnabled: false,
        clientName: invoice.ClientName,
        amount: invoice.Amount
    }
  }

  enableEdit = (invoiceID) => {
    this.setState({
        editEnabled: true,
    });
  }

  saveChanges = () => {
    this.props.updateInvoice(this.props.ID)
  }

  render() {
    const {invoice} = this.props;
    const {editEnabled, clientName, amount} = this.state;

    return (
         <div key={invoice.ID}>
            <span className="cell">{invoice.ID}</span>
            <span className="cell">
                {editEnabled
                    ? <input type="text" name="clientName" value={clientName} />
                    : clientName
                }
            </span>
            <span className="cell">
                {editEnabled
                    ? <input type="text" name="amount" value={amount} />
                    : amount
                }
            </span>
            <span className="cell">
                {invoice.isPaid ? 'Paid' : 'Not Paid'}
            </span>
            <span className="cell">
                {!editEnabled
                    ?  <button onClick={this.enableEdit}>Edit</button>
                    :  <button onClick={this.saveChanges}>Save</button>
                }
            </span>
        </div>
    );
  }
}

export default Invoice;
